using System;
using System.Collections.Generic;
using System.Text;
using System.Linq;

namespace AOC2016
{
    public class Day2
    {
        public static void Main()
        {
            var input = @"



";
            var tableau = new string[5] { "     ", " 123 ", " 456 ", " 789 ", "     " };
            var tableau2 = new string[7] { "       ", "   1   ", "  234  ", " 56789 ", "  ABC  ", "   D   ", "       " };

            Console.WriteLine(extractCode(input, tableau, 2, 2));
            Console.WriteLine(extractCode(input, tableau2, 3, 1));

        }

        private static string extractCode(string input, string[] tableau, int x, int y)
        {
            var code = new StringBuilder();
            foreach (var c in input)
            {
                int nx = x, ny = y;
                switch (c)
                {
                    case 'U': nx--; break;
                    case 'D': nx++; break;
                    case 'L': ny--; break;
                    case 'R': ny++; break;
                }
                if (tableau[nx][ny] != ' ')
                {
                    x = nx;
                    y = ny;
                }
                if (c == '\n')
                    code.Append(tableau[x][y]);
            }
            code.Append(tableau[x][y]);
            return code.ToString();
        }
    }
}
